--Creare tabele entitati
CREATE TABLE users
(
    id NUMBER CONSTRAINT id_users_primary_key PRIMARY KEY,
    username VARCHAR2(30) NOT NULL CONSTRAINT username_must_be_unique UNIQUE,
    password CHAR(32) CONSTRAINT password_not_null NOT NULL,
    e_mail VARCHAR2(320) NOT NULL CONSTRAINT e_mail_must_be_unique UNIQUE, --64@255
    rank CHAR(1) NOT NULL CONSTRAINT rank_must_be_valid CHECK (rank IN ('u', 'm', 'a')), -- user / moderator / admin
    birth_date DATE NOT NULL,
    origin_country VARCHAR2(50),
    created_at DATE DEFAULT CURRENT_DATE,
    updated_at DATE DEFAULT CURRENT_DATE
)
/
CREATE TABLE movies
(
    id NUMBER CONSTRAINT id_movies_primary_key PRIMARY KEY,
    title VARCHAR2(150) NOT NULL,
    image BLOB NOT NULL,
    release_date DATE NOT NULL,
    duration INTERVAL DAY TO SECOND NOT NULL,
    parental_rating VARCHAR2(2),
    origin_country VARCHAR2(50),
    language VARCHAR2(2),
    budget NUMBER,
    created_at DATE DEFAULT CURRENT_DATE,
    updated_at DATE DEFAULT CURRENT_DATE
)
/
CREATE TABLE people
(
    id NUMBER CONSTRAINT id_people_primary_key PRIMARY KEY,
    first_name VARCHAR2(100),
    last_name VARCHAR2(100),
    birth_date DATE,
    description VARCHAR2(1000),
    origin_country VARCHAR2(50),
    created_at DATE DEFAULT CURRENT_DATE,
    updated_at DATE DEFAULT CURRENT_DATE
)
/
CREATE TABLE reviews
(
    id NUMBER CONSTRAINT id_reviews_primary_key PRIMARY KEY,
    title VARCHAR2(100) NOT NULL,
    description VARCHAR2(1000) NOT NULL,
    review_date DATE NOT NULL,
    likes NUMBER DEFAULT 0,
    dislikes NUMBER DEFAULT 0,
    spoiler NUMBER(1) DEFAULT 0,
    created_at DATE DEFAULT CURRENT_DATE,
    updated_at DATE DEFAULT CURRENT_DATE
)
/
CREATE TABLE roles
(
    id NUMBER CONSTRAINT id_roles_primary_key PRIMARY KEY,
    role VARCHAR2(50) NOT NULL CONSTRAINT role_must_be_unique UNIQUE,
    created_at DATE DEFAULT CURRENT_DATE,
    updated_at DATE DEFAULT CURRENT_DATE
)
/
CREATE TABLE ratings
(
    id NUMBER CONSTRAINT id_ratings_primary_key PRIMARY KEY,
    rating NUMBER(2) NOT NULL,
    created_at DATE DEFAULT CURRENT_DATE,
    updated_at DATE DEFAULT CURRENT_DATE
)
/
