--Creare tabele relatii
CREATE TABLE user_lists
(
    id NUMBER CONSTRAINT id_user_lists_primary_key PRIMARY KEY,
    id_user NUMBER,
    id_list NUMBER,
    created_at DATE DEFAULT CURRENT_DATE,
    updated_at DATE DEFAULT CURRENT_DATE,
    
    CONSTRAINT fk_user_lists_id_user FOREIGN KEY (id_user) REFERENCES users(id) ON DELETE CASCADE,
